mapper.select:
SELECT H.*, HS.average
, IF(B.MNO = #{mno},'Y','N') AS BOOKMARK
FROM HERITAGE H LEFT JOIN HERIBOOKMARK B ON(H.HNO = B.HNO) LEFT JOIN ( SELECT hno, AVG(rating) AS average FROM HeritageStars GROUP BY hno ) HS ON (H.hNo = HS.hno) WHERE 1=1 AND H.hNo = #{no};

SELECT
	COUNT(*) FROM HERIBOOKMARK
WHERE 1 = 1
AND mno = #{mno}
AND hNo = #{hNo}

mapper.delete:
DELETE FROM HERIBOOKMARK
WHERE 1 = 1
AND mno = #{mno}
AND hNo = #{hNo}

mapper.select:
SELECT COUNT(*) as replyCount
FROM REPLY
WHERE hNo = #{hNo}
